A=(0,10);
B=(5.9,-8.1);
C=(-9.5,3.1);
D=(9.5,3.1);
E=(-5.9,-8.1);
draw(A--B--C--D--E--cycle,linewidth(0.7));
label("7",A,N);
label("6",B,SE);
label("5",C,NW);
label("9",D,NE);
label("3",E,SW);
label("14",(0,1.1),N);
label("13",(0.7,0),NE);
label("10",(-0.7,0),NW);
label("11",(0,-0.7),SW);
label("12",(0,-0.7),SE);
[/asy]
